settings.custom_liquid:
{% assign gallery_images = article.metafields.custom.gallery_images.value %}

<div class="gallery">
{% for item in gallery_images %}

<div class="gallery-item">

<img src="{{ item | image_url }}">
</div>
{% endfor %}
</div>

<style>
.gallery {
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top:20px;
  margin-bottom:50px;
}

.gallery-item {
  flex: 1 0 calc(25% - 10px); /* 4 items per row */
  box-sizing: border-box;
}

.gallery-item img {
  width: 100%;
  height: auto;
  display: block;
}

@media (max-width: 800px) {
  .gallery-item {
    flex: 1 0 calc(50% - 10px); /* 2 items per row on tablets */
  }
}

@media (max-width: 500px) {
  .gallery-item {
    flex: 1 0 100%; /* 1 item per row on mobile */
  }
}
</style>
